INSERT INTO data_licenses (id, name, description) VALUES (2, 'Creative Commons Attribution License', 'A Creative Commons (CC) license is one of several public copyright licenses that enable the free distribution of an otherwise copyrighted "work".[a] A CC license is used when an author wants to give other people the right to share, use, and build upon a work that the author has created. CC provides an author flexibility (for example, they might choose to allow only non-commercial uses of a given work) and protects the people who use or redistribute an author''s work from concerns of copyright infringement as long as they abide by the conditions that are specified in the license by which the author distributes the work.');