INSERT INTO data_publishers (id, name, abbreviation, description) VALUES (1, 'NatureScot', 'SNH', 'NatureScot, which was formerly known as Scottish Natural Heritage, is the public body responsible for Scotland''s natural heritage, especially its natural, genetic and scenic diversity. It advises the Scottish Government and acts as a government agent in the delivery of conservation designations, i.e. national nature reserves, local nature reserves, national parks, Sites of Special Scientific Interest (SSSIs), Special Areas of Conservation, Special Protection Areas and the national scenic areas.');
INSERT INTO data_publishers (id, name, abbreviation, description) VALUES (2, 'SpatialData.gov.scot', NULL, 'Scotlandâ€™s catalogue of spatial data where you can find, share and reuse spatial data provided by Scottish public sector organisations.');
INSERT INTO data_publishers (id, name, abbreviation, description) VALUES (3, 'EDF Renewables On Shore & Solar Development', NULL, 'Data produced in house.');
INSERT INTO data_publishers (id, name, abbreviation, description) VALUES (4, 'Natural England', NULL, 'Natural England is a non-departmental public body in the United Kingdom sponsored by the Department for Environment, Food and Rural Affairs. It is responsible for ensuring that England''s natural environment, including its land, flora and fauna, freshwater and marine environments, geology and soils, are protected and improved. It also has a responsibility to help people enjoy, understand and access the natural environment.');
INSERT INTO data_publishers (id, name, abbreviation, description) VALUES (5, 'Joint Nature Conservation Committee', 'JNCC', 'The JNCC is the public body that advises the UK Government and devolved administrations on UK-wide and international nature conservation.');
INSERT INTO data_publishers (id, name, abbreviation, description) VALUES (6, 'Historic England', NULL, 'Historic England (officially the Historic Buildings and Monuments Commission for England) is an executive non-departmental public body of the British Government sponsored by the Department for Digital, Culture, Media and Sport. It is tasked with protecting the historic environment of England by preserving and listing historic buildings, scheduling ancient monuments, registering historic Parks and Gardens and by advising central and local government.');
INSERT INTO data_publishers (id, name, abbreviation, description) VALUES (7, 'Historic Environment Scotland', 'HES', 'Historic Environment Scotland is an executive non-departmental public body responsible for investigating, caring for and promoting Scotland''s historic environment.');
INSERT INTO data_publishers (id, name, abbreviation, description) VALUES (8, 'Ministry of Housing, Communities and Local Government', 'DLUHC', 'The Department for Levelling Up, Housing and Communities (DLUHC), formerly the Ministry for Housing, Communities and Local Government (MHCLG),[2] is a department of His Majesty''s Government responsible for housing, communities, local government in England and the levelling up policy. It was established in May 2006 and is the successor to the Office of the Deputy Prime Minister, established in 2001.');
INSERT INTO data_publishers (id, name, abbreviation, description) VALUES (9, 'Forestry Commission', '', '');
INSERT INTO data_publishers (id, name, abbreviation, description) VALUES (10, 'Department for Environment Food and Rural Affairs', 'DEFRA', 'The Department for Environment, Food and Rural Affairs (Defra) is a department of His Majesty''s Government in the United Kingdom responsible for environmental protection, food production and standards, agriculture, fisheries and rural communities in the entire United Kingdom. Concordats set out agreed frameworks for co operation, between it and the Scottish Government, Welsh Government and Northern Ireland Executive, which have devolved responsibilities for these matters in their respective nations.');